script:
$(document).ready(function() {
  var $content_container = $("#content_container");
  var $anim_container = $("#animation_container");
  var $header_container = $("#header-container");
  var $_preload_div_ = $("#_preload_div_");
  var w = $anim_container.width(), h = $anim_container.height();
  var iw = $content_container.width(), ih = $content_container.height();
  var pRatio = window.devicePixelRatio || 1, xRatio=iw/w, yRatio=ih/h, sRatio=1, psRatio=1;
  if (iw < 950) {
    if (iw > 714) {
      sRatio = 1;
      psRatio = 1;
    } else {
      sRatio = iw/714;
      psRatio = 0.91072;
    }
  }	else {
    sRatio = xRatio;
  }

  $anim_container.width(w*sRatio+'px');
  $anim_container.height(h*sRatio+'px');
  $_preload_div_.width(iw*psRatio+'px');
  $_preload_div_.height(ih*psRatio+'px');
  $_preload_div_.css('left', ((iw-(iw*psRatio))/2)+'px');

  if ($.browser.mobile === true || iw < 750) {
  } else {
    //$header_container.hide();
  }

});
